function insertion(input) {
	for(var i=1,len=input.length;i<len;i++){	
		if(input[i]<input[i-1]){
			var temp = input[i];
			var j=i;
			while(j>0&&input[j-1]>temp){
				input[j] = input[j-1];
				j--;
			}
			input[j] = temp;
		}
	}
	return input;
}
